from sympy import *from sympy.plotting import plot3d# x = symbols('x')# f = x**2 + 1# plot(f)x, y = symbols('x y')f = 2*x + 3*yplot3d(f)summation = sum(2*i for i in range(1,6))print(summation)## Logarithmsfrom math import log# 2 raised to what power gives me 8?x = log(8, 2)print(x) # Euler's Numberfrom math import exp p = 1000 # principal, starting amountr = .05 # interest rate, by yeart = 3.0 # time, number of yearsa = p * exp(r*t)print(a)p = 1000r = .05n = 12t = 3a = p * (1+(r/n))**(n*t)print(a)#  Natural Logarithmsfrom math import log#  e raised to what power gives us 10?x = log(10)print(x)# Derivativesfrom sympy import *# Declare 'x' to SymPyx = symbols('x')#  Now just use Python syntax to declare functionf = x**2# Calulate the derivative of the functiondx_f = diff(f)print(dx_f)x, y = symbols('x y')f = 2*x**3 + 3*y**3# Calculate the partial derivatives for x and ydx_f = diff(f, x)dy_f = diff(f, y)print(dx_f)print(dy_f)plot3d(f)# Integrationx = symbols('x')f = x**2 + 1# Calculate the integral of the function w.r.t. x for the area between x=0 and 1area = integrate(f, (x, 0, 1))print(area)x = symbols('x')f = 3*x**2 + 1area = integrate(f, (x, 0, 2))print(area)